import { useEffect, useState, useRef } from "react";
import * as THREE from "three";
import { OBJLoader } from "three/examples/jsm/loaders/OBJLoader.js";

// Add declaration for window.renderer
declare global {
  interface Window {
    renderer?: {
      xr?: {
        isPresenting: boolean;
        addEventListener: (event: string, listener: () => void) => void;
      };
      setAnimationLoop: (callback: (() => void) | null) => void;
    };
  }
}

interface FallingPillarsProps {
  scene: THREE.Scene;
  camera?: THREE.Camera;
}

// Images en base64
const GLOBE_SVG_BASE64 =
  "data:image/jpeg;base64,";
const WINDOW_SVG_BASE64 =
  "data:image/jpeg;base64,";
export default function FallingPillars({ scene, camera }: FallingPillarsProps) {
  const [pillars, setPillars] = useState<THREE.Group[]>([]);
  const cameraRef = useRef<THREE.Camera | null>(null);
  const pedestalModelRef = useRef<THREE.Group | null>(null);

  useEffect(() => {
    if (!scene) return;

    // Utiliser la camra passe en prop ou rechercher dans la scne
    cameraRef.current = camera || null;

    // Chercher la camra dans la scne si elle n'est pas fournie
    if (!cameraRef.current) {
      scene.traverse((object) => {
        if (
          object instanceof THREE.PerspectiveCamera ||
          object instanceof THREE.OrthographicCamera
        ) {
          cameraRef.current = object;
        }
      });
    }

    // Vrifier si nous sommes en mode VR
    let isVRSession = false;

    // Function pour vrifier l'tat VR - appele lors de chaque frame
    function checkVRState() {
      // Vrifier si le renderer est en session XR
      if (
        window.renderer &&
        window.renderer.xr &&
        window.renderer.xr.isPresenting
      ) {
        isVRSession = true;
      } else {
        isVRSession = false;
      }
    }

    // Prcharger le modle OBJ du pidestal romain
    const objLoader = new OBJLoader();
    objLoader.load(
      "/pedestal/roman_pedestal.obj",
      (object) => {
        // Appliquer le matriau au modle
        object.traverse((child) => {
          if (child instanceof THREE.Mesh) {
            child.material = new THREE.MeshPhongMaterial({
              color: 0xdddddd,
              specular: 0x111111,
              shininess: 100,
            });
          }
        });

        // Ajuster l'chelle et l'orientation si ncessaire
        object.scale.set(0.1, 0.1, 0.1);

        // Stocker le modle pour une utilisation ultrieure
        pedestalModelRef.current = object;
      },
      (xhr) => {
        console.log((xhr.loaded / xhr.total) * 100 + "% loaded");
      },
      (error) => {
        console.error("Error loading OBJ model:", error);
      }
    );

    // Cration des gomtries et matriaux pour les piliers avec effet marbre
    const pillarGeometry = new THREE.CylinderGeometry(0.3, 0.3, 0.8, 32);

    // Matriau de base blanc pour simuler le marbre (avec transparence)
    const pillarMaterial = new THREE.MeshPhongMaterial({
      color: 0xffffff,
      specular: 0x111111,
      shininess: 100,
      reflectivity: 1,
      emissive: 0x444444,
      transparent: true,
      opacity: 1, // On commencera avec une opacit pleine puis on la rduira
    });

    // Paramtres d'animation
    const fallDelay = 1000;
    let lastFallTime = Date.now() + 2000;
    let currentPillarIndex = 0;
    const createdPillars: THREE.Group[] = [];
    const pillarLights: THREE.SpotLight[] = [];
    const floatingImages: {
      mesh: THREE.Mesh;
      group: THREE.Group;
      pillarIndex: number;
    }[] = [];
    const placedPedestals: THREE.Group[] = [];

    // Suivre quels piliers ont dj leurs images cres et lesquels ont t remplacs
    const imageCreated: boolean[] = [];
    const pedestalPlaced: boolean[] = [];

    // Chargeur de textures pour les images
    const textureLoader = new THREE.TextureLoader();

    // Tableau des images en base64 au lieu des chemins
    const imageBase64Sources = [GLOBE_SVG_BASE64, WINDOW_SVG_BASE64];

    // Fonction pour placer le modle 3D OBJ  l'endroit du pilier et rendre le pilier transparent
    function placeObjectAndFadePillar(pillarIndex: number) {
      // Vrifier si un pidestal a dj t plac pour ce pilier
      if (pedestalPlaced[pillarIndex]) return;

      // Vrifier si le modle est charg
      if (!pedestalModelRef.current) {
        console.warn("Modle de pidestal non charg");
        return;
      }

      const pillarGroup = createdPillars[pillarIndex];

      // Cloner le modle OBJ pour chaque pilier
      const pedestalModel = pedestalModelRef.current.clone();

      // Rduire l'chelle du pidestal - il est trop grand
      pedestalModel.scale.set(0.02, 0.02, 0.02); // Rduction significative de l'chelle

      // Copier exactement la position X et Z du pilier, mais fixer Y  -20 (sol)
      const pillarPosition = pillarGroup.position.clone();
      pedestalModel.position.set(
        pillarPosition.x - 0.1,
        -0.5, // Position Y au niveau du sol
        pillarPosition.z - 0.1
      );

      // Copier exactement la rotation du pilier
      pedestalModel.rotation.copy(pillarGroup.rotation);

      // Pour le debugging
      console.log(
        `Pilier position: ${pillarPosition.x}, ${pillarPosition.y}, ${pillarPosition.z}`
      );
      console.log(
        `Pidestal plac : ${pedestalModel.position.x}, ${pedestalModel.position.y}, ${pedestalModel.position.z}`
      );

      // Ajouter  la scne
      scene.add(pedestalModel);
      placedPedestals.push(pedestalModel);

      // Rendre le pilier existant transparent progressivement
      const pillarMesh = pillarGroup.children.find(
        (child) => child instanceof THREE.Mesh
      ) as THREE.Mesh;
      if (
        pillarMesh &&
        pillarMesh.material instanceof THREE.MeshPhongMaterial
      ) {
        // Animation de fade out sur 1 seconde
        // Note: Pour rendre le pidestal plus petit, il faut modifier l'chelle ailleurs
        const startTime = Date.now();
        const duration = 1000; // ms
        const fadeOut = () => {
          const elapsed = Date.now() - startTime;
          const progress = Math.min(elapsed / duration, 1);

          // Rduire l'opacit progressivement
          (pillarMesh.material as THREE.MeshPhongMaterial).opacity =
            1 - progress;

          if (progress < 1) {
            requestAnimationFrame(fadeOut);
          } else {
            // Compltement transparent, on peut cacher le pilier
            pillarMesh.visible = false;
          }
        };

        fadeOut();
      }

      // Marquer ce pilier comme trait
      pedestalPlaced[pillarIndex] = true;
    }

    // Cration des piliers initiaux
    function createInitialPillars() {
      const numberOfPillars = 2;
      const radius = 4;
      const arcAngle = Math.PI / 3;

      for (let i = 0; i < numberOfPillars; i++) {
        const angle = -Math.PI / 6 + (i * arcAngle) / (numberOfPillars - 1);
        const x = radius * Math.cos(angle);
        const z = radius * Math.sin(angle);

        const pillar = new THREE.Mesh(pillarGeometry, pillarMaterial.clone()); // Clone du matriau pour chaque pilier
        const pillarGroup = new THREE.Group();
        pillarGroup.add(pillar);

        pillarGroup.position.set(x, 20, z);
        pillarGroup.userData.shouldFall = false;
        pillarGroup.userData.landed = false;

        pillarGroup.lookAt(0, 20, -15);

        // Ajouter une lumire au-dessus du pilier
        const pillarLight = new THREE.SpotLight(
          0xffffdd,
          8,
          15,
          Math.PI / 4,
          0.5,
          1
        );
        pillarLight.position.set(0, 6, 0);
        pillarLight.target = pillar;

        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(0, 10, 0);
        directionalLight.target = pillar;
        pillarGroup.add(directionalLight);

        pillarGroup.add(pillarLight);
        pillarLights.push(pillarLight);

        scene.add(pillarGroup);
        createdPillars.push(pillarGroup);

        // Stocker l'index du pilier pour rfrence future
        pillarGroup.userData.pillarIndex = i;

        // Initialiser le tracking des images cres et des pidestaux placs
        imageCreated[i] = false;
        pedestalPlaced[i] = false;
      }

      setPillars(createdPillars);
    }

    // Crer une image au-dessus d'un pilier tomb
    function createImageAbovePillar(pillarIndex: number) {
      // Vrifier que l'image n'a pas dj t cre pour ce pilier
      if (imageCreated[pillarIndex]) return;

      if (
        pillarIndex >= imageBase64Sources.length ||
        pillarIndex >= createdPillars.length
      )
        return;

      const pillar = createdPillars[pillarIndex];

      // Marquer cette image comme cre pour viter les duplications
      imageCreated[pillarIndex] = true;

      // Charger la texture directement  partir de la source base64
      const texture = new THREE.TextureLoader().load(GLOBE_SVG_BASE64);
      texture.format = THREE.RGBAFormat; // Forcer le format RGBA pour la compatibilit
      texture.minFilter = THREE.LinearFilter; // Dsactiver le mipmapping
      texture.magFilter = THREE.LinearFilter;
      texture.needsUpdate = true; // Forcer la mise  jour de la texture

      const spriteMaterial = new THREE.SpriteMaterial({
        map: texture,
        color: 0xffffff, // Couleur blanche pour ne pas altrer la texture
        transparent: true,
        opacity: 1.0, // Assurez-vous que l'opacit est  1
        depthTest: false, // Dsactiver le depth test pour viter les problmes de rendu
        depthWrite: false, // Ne pas crire dans le depth buffer
        sizeAttenuation: true, // L'chelle change avec la distance
      });

      // Crer un sprite au lieu d'un mesh plan
      const sprite = new THREE.Sprite(spriteMaterial);
      sprite.scale.set(1.5, 1.5, 1);

      // Crer un groupe pour contenir le sprite et les effets
      const imageGroup = new THREE.Group();
      imageGroup.add(sprite);

      // Positionner l'image juste au-dessus du pidestal
      imageGroup.position.set(
        pillar.position.x - 0.1,
        1.5,
        pillar.position.z - 0.1
      );

      // Ajouter une lumire trs puissante pour s'assurer de la visibilit en VR
      const imageLight = new THREE.PointLight(0xffffff, 5, 3);
      imageLight.position.set(0, 0, 0);
      imageGroup.add(imageLight);

      // Ajouter un clairage secondaire color
      const colorLight = new THREE.PointLight(
        0x3388ff, // Couleur bleue pour le premier pilier
        2,
        2
      );
      colorLight.position.set(0, 0.5, 0);
      imageGroup.add(colorLight);

      scene.add(imageGroup);

      floatingImages.push({
        mesh: sprite as unknown as THREE.Mesh, // forcer le typage puisque nous utilisons Sprite
        group: imageGroup,
        pillarIndex: pillarIndex,
      });
    }

    // Animation des piliers et des images
    function animatePillars() {
      checkVRState(); // Vrifier l'tat VR  chaque frame

      // Utiliser soit le timestamp pass  la fonction, soit l'horloge systme
      // En VR, XR gre sa propre animation, donc on doit viter les timers bass sur Date.now()
      const currentTime =
        window.renderer && window.renderer.xr && window.renderer.xr.isPresenting
          ? window.performance.now()
          : Date.now();

      // Le reste du code d'animation mais avec currentTime modifi pour tre compatible VR
      // Vrifier si c'est le moment de faire tomber un nouveau pilier - adaptation pour VR
      if (
        currentPillarIndex < createdPillars.length &&
        currentTime - lastFallTime > fallDelay
      ) {
        createdPillars[currentPillarIndex].userData.shouldFall = true;
        lastFallTime = currentTime;
        currentPillarIndex++;
      }

      // Animer les piliers - utilisation d'une vitesse de chute base sur le framerate, pas sur le temps
      createdPillars.forEach((pillarGroup, index) => {
        if (pillarGroup.userData.shouldFall && pillarGroup.position.y > 0) {
          // En VR, utiliser une vitesse constante par frame plutt qu'une valeur fixe
          const fallSpeed = isVRSession ? 0.1 : 1; // Ajuster selon les besoins
          pillarGroup.position.y -= fallSpeed;

          // Arrter au niveau du sol
          if (pillarGroup.position.y < -20) {
            pillarGroup.position.y = -20;

            // Si le pilier n'tait pas dj marqu comme atterri
            if (!pillarGroup.userData.landed) {
              pillarGroup.userData.landed = true;

              // Flash lumineux  l'impact
              if (pillarLights[index]) {
                pillarLights[index].intensity = 15;
                setTimeout(() => {
                  if (pillarLights[index]) {
                    pillarLights[index].intensity = 0;
                  }
                }, 200);
              }
            }
          }

          setTimeout(() => {
            placeObjectAndFadePillar(index);
            createImageAbovePillar(index);
          }, 500); // Dlai avant de crer l'image
        }
      });

      // Orientation des images vers la camra
      if (cameraRef.current) {
        const cameraPosition = new THREE.Vector3();
        cameraRef.current.getWorldPosition(cameraPosition);

        floatingImages.forEach((imageData) => {
          if (!imageData.group) return;

          // Pas besoin d'orienter les sprites, ils font face automatiquement  la camra
          // imageData.group.lookAt(cameraPosition);

          // Lgre animation de flottement - ajuste pour la nouvelle position Y de base
          const pillarIndex = imageData.pillarIndex;

          // Animation de flottement au-dessus du pidestal
          const floatSpeed = isVRSession ? 0.0005 : 0.001;
          imageData.group.position.y =
            1.5 + Math.sin(currentTime * floatSpeed) * 0.2;

          // Animation de pulse pour la taille du sprite
          if (imageData.mesh) {
            const pulseSpeed = isVRSession ? 0.001 : 0.002;
            const pulseScale = 1 + Math.sin(currentTime * pulseSpeed) * 0.1;
            imageData.mesh.scale.set(1.5 * pulseScale, 1.5 * pulseScale, 1);
          }
        });
      }
    }

    // Ajouter une lumire ambiante
    const ambientLight = new THREE.AmbientLight(0xcccccc, 1.5);
    scene.add(ambientLight);

    // Crer les piliers initiaux
    createInitialPillars();

    // Animation frame function - modifie pour supporter WebXR
    let animationFrameId: number;

    // Se connecter  l'animation XR si disponible, sinon utiliser requestAnimationFrame standard
    function animate() {
      if (window.renderer && window.renderer.xr) {
        // Si renderer.xr existe, s'assurer que notre fonction est appele lors de l'animation XR
        window.renderer.xr.addEventListener("sessionstart", () => {
          console.log("Session XR commence - animations adaptes");
          isVRSession = true;
        });

        window.renderer.xr.addEventListener("sessionend", () => {
          console.log("Session XR termine");
          isVRSession = false;
        });

        // S'assurer que animatePillars est appel  chaque frame rendue
        window.renderer.setAnimationLoop(() => {
          animatePillars();
        });
      } else {
        // Mthode classique pour l'animation non-VR
        function animateFrame() {
          animationFrameId = requestAnimationFrame(animateFrame);
          animatePillars();
        }
        animateFrame();
      }
    }

    // Dmarrer l'animation
    animate();

    // Cleanup  la dsinstanciation du composant
    return () => {
      // Arrter les animations
      if (window.renderer && window.renderer.xr) {
        window.renderer.setAnimationLoop(null);
      } else {
        cancelAnimationFrame(animationFrameId);
      }

      // Supprimer les piliers de la scne
      createdPillars.forEach((pillar) => {
        scene.remove(pillar);
      });

      // Supprimer les images flottantes
      floatingImages.forEach((imageData) => {
        if (imageData.group) {
          scene.remove(imageData.group);
        }
      });

      // Supprimer les pidestaux placs
      placedPedestals.forEach((pedestal) => {
        scene.remove(pedestal);
      });

      // Supprimer la lumire ambiante
      scene.remove(ambientLight);
    };
  }, [scene, camera]);

  return null;
}
